function ScreenSize(e){if(e.matches){const e={damping:.04,delegateTo:document},t=Scrollbar.init(document.querySelector(".main"),e);t.addListener(({offset:e})=>{document.querySelector(".navbar").style.top=e.y+"px",document.querySelector(".navbar").style.left=e.x+"px",document.querySelector(".sidebar").style.top=e.y+"px",document.querySelector(".sidebar").style.right=e.x+"px"})}else Scrollbar.destroy()}const mediaQuery=window.matchMedia("(min-width: 740px )");mediaQuery.addListener(ScreenSize),ScreenSize(mediaQuery);